
function notify(id) {
    let notifyElement = $('#' + id);

    console.log(id);

    $('.custom-social-proof').stop();
    notifyElement.slideToggle('slow');
    notifyElement.find(".custom-close").click(function () {
        $('.custom-social-proof').stop();
        notifyElement.slideToggle('slow');
    });

    // ِclose after five seconds
    return notifyElement;
}

